@media (orientation:portrait) {
    #ghx-work {
        height: calc(100% - 40px) !important;
        display: block;
    }
    #ghx-detail-view {
        width: auto !important;
        display: block;
        height: 50%;
    }
    #ghx-pool-column {
        width: auto !important;
        display: block;
        height: 50%;
        overflow: hidden;
    }
    #ghx-rabid {
        height: calc(100vh - 148px);
    }
    #ghx-pool,
    #ghx-detail-contents {
        height: 100%;
    }
    #ghx-detail-contents {
        border-left: none;
        border-top: 2px solid #ccc;
    }
    #ghx-detail-head {
        width: calc(100% - 20px);
    }
}
